dplyr::tibble(
  n = p(1:6, "."),
  `File Name` = c(
    p(
      "<b>RVU</b>" #,
      # "<b>[</b>",
      # "<small>YY</small>",
      # "<b>][</b>",
      # "<small>VER</small>",
      # "<b>]</b>"
      ), 
    p(
      "<b>PPRRVU</b>" #,
      # "<b>[</b>",
      # "<small>YY</small>",
      # "<b>]</b>"
      ), 
    p(
      "<b>GPCI</b>" #, 
      # "<b>[</b>",
      # "<small>YYYY</small>",
      # "<b>]</b>"
      ), 
    p(
      # "<b>[</b>",
      # "<small>YY</small>",
      # "<b>]</b>"
      "<b>LOCCO</b>"
      ), 
    p(
      "<b>ANES</b>" #,
      # "<b>[</b>",
      # "<small>YYYY</small>",
      # "<b>]</b>"
           ), 
    p("<b>OPPSCAP</b>")),
  Description = c(
    "Data Dictionary, Record Layout and File Documentation",
    "Physician Fee Schedule RVUs and Policy Indicators",
    "Geographic Practice Cost Index Components for each Carrier/Locality combination",
    "Locality to County Crosswalk",
    "Anesthesia Conversion Factors",
    "OPPS-based capitation-adjusted payment amounts.")) |> 
  gt::gt(
    rowname_col = "n",
    process_md = TRUE) |> 
  gt::cols_width(
    n ~ gt::px(70),
    `File Name` ~ gt::px(100)) |> 
  gt::fmt_markdown() |> 
  gt::opt_all_caps() |> 
  gt::tab_header("PFS Relative Value File") |>
  gt::opt_all_caps() |>
  gt::tab_options(
    heading.align = "left",
    heading.border.bottom.color = "#555555", # "#4758AB",
    heading.title.font.weight = "bold",
    heading.background.color = "#555555", # "#f8f9fa",
