## Indicators




::: {.cell layout-align="center"}

```{.r .cell-code}
rvu_splits <- rvu24a |>  
  hacksaw::count_split(
    mod,
    status_code,
    not_used_for_medicare_payment,
    non_fac_indicator,
    facility_indicator,
    pctc_ind,
    glob_days,
    mult_proc,
    bilat_surg,
    asst_surg,
    co_surg,
    team_surg,
    endo_base,
    physician_supervision_of_diagnostic_procedures,
    diagnostic_imaging_family_indicator
  )

process_splits <- function(split) {

  split_name <- \(split) {

  varname <- rlang::names2(split)[1]

  split |> 
    dplyr::mutate(variable = !!varname, .before = 1) |> 
    purrr::set_names(c("variable", "value", "n"))
  }

  purrr::map(rvu_splits, split_name) |> 
  purrr::list_rbind() |> 
  dplyr::mutate(pct = n / sum(n), .by = variable)

}

rvu_vars <- process_splits(rvu_splits)

vars <- rvu_vars |> 
  dplyr::mutate(
    variable = stringr::str_replace_all(
      variable, "_", " ") |> 
      stringr::str_to_upper(),
  )

idx <- c(
