    ins_prim,
    pts = patients,
    vpp = visits / patients,
    ppp = procedures / patients,
    rpp = revenue / patients,
    ppv = procedures / visits,
    rpv = revenue / visits,
    rpp = revenue / procedures
  ) |&gt; 
  arrange(desc(pts)) |&gt;
  gt(rowname_col = "ins_prim") |&gt; 
