qualify_dos &lt;- function(df, dos, date) {

  arg  &lt;- rlang::caller_arg(dos)
  call &lt;- rlang::caller_env()

  x &lt;- vctrs::vec_slice(df, df[[dos]] &gt; date)

  if (vctrs::vec_is_empty(x))  {
    cli::cli_inform(
      "0 matches for {.strong {.val {rlang::sym(dos)}}} &gt; {.val {as.Date(date)}}", 
      arg = arg,
      call = call,
      class = "qualify_dos"
      )
  }
  x
}

