qualify_date &lt;- function(df) {

  arg  &lt;- rlang::caller_arg(df)
  call &lt;- rlang::caller_env()

  x &lt;- vctrs::vec_slice(df, df[["dos"]] &gt;= as.Date("2019-01-01"))

  if (vctrs::vec_is_empty(x))  {
    cli::cli_abort(
      "No matches for {.strong dos} &gt;= {.val {as.Date('2019-01-01')}} in {.arg {arg}}.", 
      arg = arg,
      call = call,
      class = "check_length"
      )
  }
  return(x)
}


